import React, { useEffect, useState } from 'react';
import '../AgentDashboard.css'; // Importing custom CSS for styling

const AgentDashboard = ({ username }) => {
  const [messages, setMessages] = useState([]);
  const [reply, setReply] = useState(''); // State to store agent's reply

  // Fetch messages from the backend
  useEffect(() => {
    fetch('http://localhost:5000/api/messages')  // Adjust to your API route
      .then((response) => response.json())
      .then((data) => setMessages(data))
      .catch((err) => console.error('Error fetching messages:', err));
  }, []);

  // Handle reply submission
  const handleReply = (messageId) => {
    const messageToUpdate = messages.find((msg) => msg._id === messageId);
    if (messageToUpdate) {
      const updatedMessage = {
        ...messageToUpdate,
        reply: reply, // Set the agent's reply
        status: 'Resolved', // Change status to resolved
      };

      fetch(`http://localhost:5000/api/messages/${messageId}`, {
        method: 'PATCH',
        body: JSON.stringify(updatedMessage),
        headers: { 'Content-Type': 'application/json' },
      })
        .then((response) => response.json())
        .then((updatedMessage) => {
          setMessages((prevMessages) =>
            prevMessages.map((msg) =>
              msg._id === updatedMessage._id ? updatedMessage : msg
            )
          );
          setReply(''); // Reset the reply input after sending
        })
        .catch((err) => console.error('Error replying to message:', err));
    }
  };

  return (
    <div className="agent-dashboard">
      <h2>Welcome, {username}!</h2>
      <h3>Customer Messages</h3>
      <table className="message-table">
        <thead>
          <tr>
            <th>User ID</th>
            <th>Timestamp</th>
            <th>Message</th>
            <th>Action</th>
            <th>Reply</th>
          </tr>
        </thead>
        <tbody>
          {messages.map((message) => (
            <tr key={message._id}>
              <td>{message.customer}</td>
              <td>{new Date(message.timestamp).toLocaleString()}</td>
              <td>{message.message}</td>
              <td>{message.status === 'Open' ? 'Pending' : 'Resolved'}</td>
              <td>
                {message.status === 'Open' ? (
                  <div>
                    <textarea
                      value={reply}
                      onChange={(e) => setReply(e.target.value)}
                      placeholder="Type your reply here..."
                    />
                    <button
                      onClick={() => handleReply(message._id)}
                      disabled={!reply}
                    >
                      Send Reply
                    </button>
                  </div>
                ) : (
                  <span>Resolved</span>
                )}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default AgentDashboard;
