import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import AgentLogin from './components/AgentLogin';
import UserQueryForm from './components/UserQueryForm';
import AgentDashboard from './components/AgentDashboard';
import MessageDetails from './components/MessageDetails';
import { getMessages } from './services/messageService';

const App = () => {
  const [agentId, setAgentId] = useState(null);
  const [messages, setMessages] = useState([]);
  const [selectedMessage, setSelectedMessage] = useState(null);

  useEffect(() => {
    if (agentId) {
      getMessages(agentId).then((data) => setMessages(data));
    }
  }, [agentId]);

  const handleLogin = (agentId) => {
    setAgentId(agentId);
  };

  const handleMessageClick = (message) => {
    setSelectedMessage(message);
  };

  return (
    <Router>
      <Routes>
        <Route path="/user" element={<UserQueryForm />} />
        <Route path="/agent-login" element={<AgentLogin onLogin={handleLogin} />} />
        <Route
          path="/agent-dashboard"
          element={<AgentDashboard messages={messages} onMessageClick={handleMessageClick} />}
        />
        <Route path="/message-details" element={<MessageDetails message={selectedMessage} />} />
      </Routes>
    </Router>
  );
};

export default App;
