// frontend/src/App.js
import React, { useState, useEffect } from 'react';
import AgentLogin from './components/AgentLogin';
import MessageForm from './components/MessageForm';
import MessageItem from './components/MessageItem';
import MessageDetails from './components/MessageDetails';  // Corrected import for MessageDetails
import { useNavigate } from 'react-router-dom'; // useNavigate for navigation in App.js

const App = () => {
  const [messages, setMessages] = useState([]);
  const [agentId, setAgentId] = useState(null);
  const [selectedMessage, setSelectedMessage] = useState(null); // New state for selected message
  const navigate = useNavigate(); // Initialize useNavigate for navigation

  // Fetch messages assigned to the logged-in agent
  useEffect(() => {
    if (agentId) {
      fetch(`http://localhost:5000/api/messages?agentId=${agentId}`)
        .then((response) => response.json())
        .then((data) => setMessages(data))
        .catch((error) => console.error('Error fetching messages:', error));
    }
  }, [agentId]);

  const handleFormSubmit = (formData) => {
    fetch('http://localhost:5000/api/messages', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(formData),
    })
      .then((response) => response.json())
      .then((data) => setMessages((prevMessages) => [...prevMessages, data]))
      .catch((error) => console.error('Error posting message:', error));
  };

  const handleLogin = (agentId) => {
    setAgentId(agentId);  // Set the logged-in agent ID
  };

  // Handle message click to show details
  const handleMessageClick = (message) => {
    setSelectedMessage(message); // Set selected message to view details
  };

  const handleResponseSubmit = (messageId, response) => {
    const cleanedMessageId = messageId.trim();
    
    fetch(`http://localhost:5000/api/messages/${cleanedMessageId}`, {
      method: 'PUT',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ response, agentId }),
    })
      .then((response) => response.json())
      .then((updatedMessage) => {
        setMessages((prevMessages) =>
          prevMessages.map((message) =>
            message._id === updatedMessage._id ? updatedMessage : message
          )
        );
      })
      .catch((error) => console.error('Error updating message:', error));
  };

  return (
    <div>
      {!agentId ? (
        <AgentLogin onLogin={handleLogin} />
      ) : (
        <>
          <h1>Welcome Agent {agentId}</h1>
          <MessageForm onSubmit={handleFormSubmit} />
          <h2>Messages:</h2>
          <ul>
            {messages.map((message) => (
              <MessageItem
                key={message._id}
                message={message}
                onResponseSubmit={handleResponseSubmit}
                isUrgent={message.urgent}
                onClick={() => handleMessageClick(message)} // Pass click handler to each message
              />
            ))}
          </ul>

          {/* Display selected message details */}
          <MessageDetails message={selectedMessage} />
        </>
      )}
    </div>
  );
};

export default App;
