import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const AgentLogin = ({ onLogin }) => {
  const [username, setUsername] = useState('');
  const navigate = useNavigate();

  const handleLogin = (e) => {
    e.preventDefault();

    // Perform your login logic here (e.g., check if the username is valid)
    if (username) {
      onLogin(username);  // Pass the username to onLogin
      navigate('/agent-dashboard');  // Redirect to dashboard after successful login
    } else {
      alert('Please enter a username');
    }
  };

  return (
    <div>
      <h2>Agent Login</h2>
      <form onSubmit={handleLogin}>
        <div>
          <label>Username:</label>
          <input
            type="text"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
          />
        </div>
        <button type="submit">Login</button>
      </form>
    </div>
  );
};

export default AgentLogin;
