// frontend/src/App.js
import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import AgentLogin from './components/AgentLogin';
import UserMessageForm from './components/UserMessageForm'; // User query submission form
import AgentDashboard from './components/AgentDashboard';  // Agent dashboard to view messages

const App = () => {
  const [agentId, setAgentId] = useState(null);

  const handleLogin = (id) => {
    setAgentId(id); // Set agent ID after successful login
  };

  return (
    <Router>
      <Routes>
        {/* User Query Submission Screen */}
        <Route path="/" element={<UserMessageForm />} />

        {/* Agent Login Screen */}
        <Route path="/agent-login" element={<AgentLogin onLogin={handleLogin} />} />

        {/* Agent Dashboard */}
        <Route
          path="/agent-dashboard"
          element={agentId ? <AgentDashboard /> : <Navigate to="/agent-login" />}
        />
      </Routes>
    </Router>
  );
};

export default App;
