import React, { useState } from 'react';
import '../styles/UserQueryForm.css';

const UserQueryForm = () => {
  const [customer, setCustomer] = useState(''); // Changed 'userId' to 'customer' to align with the backend schema
  const [message, setMessage] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!customer || !message) {
      alert('Both fields are required!');
      return;
    }

    try {
      const response = await fetch('http://localhost:5000/api/messages', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ customer, message }), // Use 'customer' instead of 'userId'
      });

      if (response.ok) {
        alert('Query submitted successfully!');
        setCustomer('');
        setMessage('');
      } else {
        alert('Failed to submit the query.');
      }
    } catch (error) {
      console.error('Error:', error);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <input
        type="text"
        value={customer}
        onChange={(e) => setCustomer(e.target.value)}
        placeholder="User ID"
        required
      />
      <textarea
        value={message}
        onChange={(e) => setMessage(e.target.value)}
        placeholder="Your message"
        required
      ></textarea>
      <button type="submit">Submit</button>
    </form>
  );
};

export default UserQueryForm;
