import React, { useState } from 'react';
import './UserQueryForm.css'; // Ensure you create this CSS file for styling

const UserQueryForm = ({ onSubmit }) => {
  const [query, setQuery] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    if (query.trim()) {
      onSubmit(query); // Calls the parent onSubmit function to handle the form data
      setQuery('');
    }
  };

  return (
    <div className="user-query-form">
      <h2>Have a Query? Ask Now!</h2>
      <form onSubmit={handleSubmit}>
        <textarea
          value={query}
          onChange={(e) => setQuery(e.target.value)}
          placeholder="Enter your query"
        />
        <button type="submit">Submit</button>
      </form>
    </div>
  );
};

export default UserQueryForm;
