import React, { useState } from 'react';
import './stylesUserQueryForm.css';

const UserQueryForm = () => {
  const [userId, setUserId] = useState('');
  const [query, setQuery] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();

    if (!userId.trim() || !query.trim()) {
      alert('Please fill in all fields');
      return;
    }

    fetch('http://localhost:5000/api/messages', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ userId, message: query }),
    })
      .then((response) => response.json())
      .then((data) => {
        console.log('Query submitted:', data);
        setUserId('');
        setQuery('');
        alert('Your query has been submitted successfully!');
      })
      .catch((error) => console.error('Error submitting query:', error));
  };

  return (
    <div className="query-form-container">
      <div className="query-form-card">
        <h1 className="query-form-title">Submit Your Query</h1>
        <form onSubmit={handleSubmit} className="query-form">
          <div className="form-group">
            <label htmlFor="userId" className="form-label">User ID</label>
            <input
              type="text"
              id="userId"
              value={userId}
              onChange={(e) => setUserId(e.target.value)}
              placeholder="Enter your User ID"
              className="form-input"
            />
          </div>
          <div className="form-group">
            <label htmlFor="query" className="form-label">Message</label>
            <textarea
              id="query"
              value={query}
              onChange={(e) => setQuery(e.target.value)}
              placeholder="Enter your query here..."
              className="form-textarea"
            />
          </div>
          <button type="submit" className="submit-button">Submit</button>
        </form>
      </div>
    </div>
  );
};

export default UserQueryForm;
