// frontend/src/components/UserQueryForm.js
import React, { useState } from 'react';
import './UserQueryForm.css'; // Import CSS

const UserQueryForm = () => {
  const [userId, setUserId] = useState('');
  const [message, setMessage] = useState('');
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!userId.trim() || !message.trim()) {
      setError('Both fields are required.');
      setSuccess('');
      return;
    }

    try {
      const response = await fetch('http://localhost:5000/api/messages', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ userId, message }),
      });

      if (response.ok) {
        setSuccess('Your query has been submitted successfully.');
        setError('');
        setUserId('');
        setMessage('');
      } else {
        setError('Failed to submit your query. Please try again.');
        setSuccess('');
      }
    } catch (err) {
      console.error('Error:', err);
      setError('An error occurred. Please try again.');
      setSuccess('');
    }
  };

  return (
    <div className="user-query-form">
      <h2>Submit Your Query</h2>
      {error && <p className="error">{error}</p>}
      {success && <p className="success">{success}</p>}

      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>User ID:</label>
          <input
            type="text"
            value={userId}
            onChange={(e) => setUserId(e.target.value)}
            placeholder="Enter your User ID"
          />
        </div>

        <div className="form-group">
          <label>Message:</label>
          <textarea
            value={message}
            onChange={(e) => setMessage(e.target.value)}
            placeholder="Enter your message"
          />
        </div>

        <button type="submit" className="submit-btn">Submit Query</button>
      </form>
    </div>
  );
};

export default UserQueryForm;
