// frontend/src/components/MessageItem.js
import React, { useState } from 'react';

const MessageItem = ({ message, onResponseSubmit, isUrgent }) => {
  const [response, setResponse] = useState('');
  const [urgent, setUrgent] = useState(isUrgent);

  const handleUrgencyToggle = () => {
    setUrgent(!urgent);  // Toggle the urgency
  };

  const handleSubmitResponse = () => {
    if (!response.trim()) {
      alert('Please enter a response before submitting!');
      return;
    }

    // Pass the message ID, response, and urgency to the parent component (App.js)
    onResponseSubmit(message._id, response, urgent);
    setResponse(''); // Clear the response field after submission
  };

  return (
    <li style={{ marginBottom: '20px', padding: '10px', border: '1px solid #ddd' }}>
      <div>
        <p><strong>Customer ID:</strong> {message.customer}</p>
        <p><strong>Message:</strong> {message.message}</p>
        <p><strong>Status:</strong> {message.status}</p>
        <p><strong>Urgent:</strong> {urgent ? 'Yes' : 'No'}</p>
        
        <textarea
          value={response}
          onChange={(e) => setResponse(e.target.value)}
          placeholder="Write your response"
          style={{ width: '100%', minHeight: '60px' }}
        />
        <br />
        
        <div>
          <button 
            onClick={handleSubmitResponse} 
            style={{ marginTop: '10px', padding: '8px 12px', backgroundColor: '#007bff', color: '#fff', border: 'none', borderRadius: '4px' }}
          >
            Submit Response
          </button>
        </div>

        <div style={{ marginTop: '10px' }}>
          <label>
            <input
              type="checkbox"
              checked={urgent}
              onChange={handleUrgencyToggle}
              style={{ marginRight: '10px' }}
            />
            Mark as Urgent
          </label>
        </div>
      </div>
    </li>
  );
};

export default MessageItem;
