// frontend/src/components/MessageItem.js
import React, { useState } from 'react';

const MessageItem = ({ message, onResponseSubmit, isUrgent }) => {
  const [response, setResponse] = useState('');

  const handleResponseChange = (e) => {
    setResponse(e.target.value); // Update the response state as the agent types
  };

  const handleSubmitResponse = (e) => {
    e.preventDefault();
    if (response.trim()) {
      onResponseSubmit(message._id, response, isUrgent); // Submit the response with the urgent flag
      setResponse(''); // Clear the response input field
    }
  };

  return (
    <li style={{ marginBottom: '20px', border: '1px solid #ccc', padding: '10px', backgroundColor: isUrgent ? '#ffcccc' : '#fff' }}>
      <div style={{ fontWeight: 'bold' }}>
        Customer ID: {message.customer} 
        {isUrgent && <span style={{ color: 'red' }}> Urgent</span>}
      </div>
      <div>{message.message}</div>
      <div>
        <small>{new Date(message.timestamp).toLocaleString()}</small>
      </div>
      
      {/* Show the message status */}
      <div style={{ marginTop: '10px', fontStyle: 'italic' }}>
        Status: <strong>{message.status}</strong>
      </div>
      
      {/* Response form only shows if message is not closed */}
      {message.status !== 'closed' && (
        <form onSubmit={handleSubmitResponse}>
          <textarea
            value={response}
            onChange={handleResponseChange}
            placeholder="Type your response here"
            rows="4"
            cols="50"
          />
          <button type="submit">Respond</button>
        </form>
      )}

      {/* Display a closed message */}
      {message.status === 'closed' && <p><strong>This message is closed.</strong></p>}
    </li>
  );
};

export default MessageItem;
