import React, { useState, useEffect } from 'react';

const AgentDashboard = () => {
  const [messages, setMessages] = useState([]); // Initialize with an empty array
  const [error, setError] = useState(null);

  useEffect(() => {
    // Fetch messages using fetch API
    fetch('http://localhost:5000/api/messages')
      .then((response) => {
        if (!response.ok) {
          throw new Error('Failed to fetch messages');
        }
        return response.json();
      })
      .then((data) => {
        setMessages(data); // Set the messages data
      })
      .catch((err) => {
        console.error('Error fetching messages:', err);
        setError('Failed to fetch messages');
      });
  }, []);

  return (
    <div>
      <h1>Agent Dashboard</h1>
      {error && <p style={{ color: 'red' }}>{error}</p>}
      {messages.length > 0 ? (
        <ul>
          {messages.map((message) => (
            <li key={message._id}>
              <strong>From:</strong> {message.customerName} <br />
              <strong>Message:</strong> {message.text} <br />
              <strong>Status:</strong> {message.status || 'Pending'}
            </li>
          ))}
        </ul>
      ) : (
        <p>No messages to display.</p>
      )}
    </div>
  );
};

export default AgentDashboard;
