import React, { useState, useEffect } from 'react';

const AgentDashboard = () => {
  const [messages, setMessages] = useState([]);
  const [loading, setLoading] = useState(true);
  const [reply, setReply] = useState('');
  const [selectedMessageId, setSelectedMessageId] = useState(null);

  useEffect(() => {
    // Fetching messages from the backend
    fetch('http://localhost:5000/api/messages')  // Your backend API endpoint
      .then(response => response.json())
      .then(data => {
        if (data.urgentMessages && data.normalMessages) {
          // Combine urgent and normal messages into one array
          setMessages([...data.urgentMessages, ...data.normalMessages]);
        } else {
          console.error("Expected urgentMessages and normalMessages but got:", data);
        }
        setLoading(false);  // Set loading to false after data is fetched
      })
      .catch(error => {
        console.error("Error fetching messages:", error);
        setLoading(false);  // Set loading to false even if there's an error
      });
  }, []);

  // Handle reply submission
  const handleReplySubmit = async (id) => {
    try {
      const response = await fetch(`http://localhost:5000/api/messages/${id}`, {
        method: 'PATCH',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ reply }), // Sending reply
      });

      const data = await response.json();
      if (data.message) {
        alert("Reply sent successfully!");
        setReply('');  // Clear the reply input
        setSelectedMessageId(null);  // Deselect the message after reply
        // Optionally, you can update the local state to reflect the resolved message
      } else {
        alert("Error replying to the message.");
      }
    } catch (error) {
      console.error("Error sending reply:", error);
    }
  };

  return (
    <div>
      <h2>Agent Dashboard</h2>
      <div>
        {loading ? (
          <p>Loading messages...</p>
        ) : messages.length > 0 ? (
          <ul>
            {messages.map((message, index) => (
              <li key={index} style={{ marginBottom: '20px', borderBottom: '1px solid #ccc' }}>
                <p><strong>User ID:</strong> {message.customer}</p>
                <p><strong>Message:</strong> {message.message}</p>
                <p><strong>Timestamp:</strong> {new Date(message.timestamp).toLocaleString()}</p>
                <p><strong>Status:</strong> {message.status}</p>

                {/* Display reply option for urgent messages or any message */}
                {message.status !== 'resolved' && (
                  <button onClick={() => setSelectedMessageId(message._id)}>
                    Reply
                  </button>
                )}

                {selectedMessageId === message._id && (
                  <div>
                    <textarea
                      value={reply}
                      onChange={(e) => setReply(e.target.value)}
                      placeholder="Type your reply here..."
                      rows="3"
                      style={{ width: '100%', marginTop: '10px' }}
                    />
                    <button
                      onClick={() => handleReplySubmit(message._id)}
                      style={{ marginTop: '10px' }}
                    >
                      Submit Reply
                    </button>
                  </div>
                )}
              </li>
            ))}
          </ul>
        ) : (
          <p>No messages to display.</p>
        )}
      </div>
    </div>
  );
};

export default AgentDashboard;
