// src/App.js
import React, { useState, useEffect } from 'react';
import MessageForm from './components/MessageForm'; // Correct the import path

const App = () => {
  const [messages, setMessages] = useState([]);

  // Fetch messages when the component mounts
  useEffect(() => {
    fetch('http://localhost:5000/api/messages')
      .then((response) => response.json())
      .then((data) => {
        setMessages(data);  // Assuming the response contains the list of messages
      })
      .catch((error) => console.error('Error fetching messages:', error));
  }, []);

  const handleFormSubmit = (formData) => {
    // Send the form data to the backend (POST request)
    fetch('http://localhost:5000/api/messages', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(formData),
    })
      .then((response) => response.json())
      .then((data) => {
        // After successfully sending data, update the messages state
        setMessages((prevMessages) => [...prevMessages, data]);
      })
      .catch((error) => console.error('Error:', error));
  };

  return (
    <div>
      <h1>Customer Service Messaging</h1>
      <MessageForm onSubmit={handleFormSubmit} />
      <h2>Messages:</h2>
      <ul>
        {messages.map((message, index) => (
          <li key={index}>
            <strong>{message.customer}:</strong> {message.message}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default App;
