import React, { useState, useEffect } from 'react';
import MessageForm from './components/MessageForm'; // Import your MessageForm component

const App = () => {
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');

  // Fetch messages when the component mounts
  useEffect(() => {
    fetch('http://localhost:5000/api/messages') // Make a GET request to backend API
      .then((response) => response.json())
      .then((data) => setMessages(data)) // Store the fetched messages in state
      .catch((error) => console.error('Error fetching messages:', error));
  }, []);

  // Handle form submission (send POST request to backend)
  const handleFormSubmit = (event) => {
    event.preventDefault();

    const formData = {
      customer: 'Customer Name', // You can replace this with a dynamic customer name if needed
      message: newMessage,
    };

    fetch('http://localhost:5000/api/messages', { // POST request to the backend
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(formData), // Send the message data as JSON
    })
      .then((response) => response.json())
      .then((data) => {
        setMessages((prevMessages) => [...prevMessages, data]);
        setNewMessage(''); // Clear the input after submitting
      })
      .catch((error) => console.error('Error posting message:', error));
  };

  return (
    <div className="app-container">
      <h1>Customer Service Messaging</h1>

      {/* Message form */}
      <form onSubmit={handleFormSubmit} className="message-form">
        <textarea
          value={newMessage}
          onChange={(e) => setNewMessage(e.target.value)}
          placeholder="Write a message..."
        />
        <button type="submit">Send</button>
      </form>

      <h2>Messages:</h2>
      <div className="message-list">
        {messages.map((message, index) => (
          <div key={index} className="message-item">
            <strong>{message.customer}</strong> -{' '}
            <em>{new Date(message.timestamp).toLocaleString()}</em>:
            <p>{message.message}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default App;
