import React, { useState } from 'react';
import '../styles/UserQueryForm.css'; // You can add styles in this separate file or use inline styles

const UserQueryForm = () => {
  const [userId, setUserId] = useState('');
  const [query, setQuery] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    // Check if both userId and query are filled
    if (!userId || !query) {
      alert('Please fill in both fields.');
      return;
    }

    // Submit the form to the backend
    fetch('http://localhost:5000/api/messages', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ userId, message: query })
    })
      .then((response) => {
        if (!response.ok) {
          throw new Error('Failed to submit the query');
        }
        return response.json();
      })
      .then((data) => {
        console.log('Query submitted:', data);
        setUserId('');
        setQuery('');
      })
      .catch((error) => {
        console.error('Error submitting query:', error);
        alert('Failed to submit your query. Please try again.');
      });
  };

  return (
    <div className="query-form-container">
      <h2>Welcome! Please Submit Your Query</h2>
      <form onSubmit={handleSubmit} className="query-form">
        <div className="form-group">
          <label htmlFor="userId">User ID</label>
          <input
            id="userId"
            type="text"
            value={userId}
            onChange={(e) => setUserId(e.target.value)}
            placeholder="Enter your User ID"
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="query">Message</label>
          <textarea
            id="query"
            value={query}
            onChange={(e) => setQuery(e.target.value)}
            placeholder="Enter your message here..."
            required
          />
        </div>
        <button type="submit" className="submit-button">Submit Query</button>
      </form>
    </div>
  );
};

export default UserQueryForm;
