// src/components/MessageForm.js
import React, { useState } from 'react';

const MessageForm = ({ onSubmit }) => {
  const [customer, setCustomer] = useState('');  // Track customer name input
  const [message, setMessage] = useState('');    // Track message content input

  // Handle form submission
  const handleSubmit = (e) => {
    e.preventDefault();  // Prevent page reload on form submit

    // Include the current timestamp
    const timestamp = new Date().toISOString();

    // Pass the form data with timestamp to the parent component
    onSubmit({ customer, message, timestamp });

    setCustomer('');  // Clear the customer name input field
    setMessage('');    // Clear the message input field
  };

  return (
    <form onSubmit={handleSubmit}>
      <input
        type="text"
        placeholder="Your Name"
        value={customer}
        onChange={(e) => setCustomer(e.target.value)}  // Update customer name state
      />
      <textarea
        placeholder="Your Message"
        value={message}
        onChange={(e) => setMessage(e.target.value)}  // Update message state
      />
      <button type="submit">Send Message</button>
    </form>
  );
};

export default MessageForm;
