// frontend/src/components/MessageItem.js
import React, { useState } from 'react';

const MessageItem = ({ message, onResponseSubmit }) => {
  const [response, setResponse] = useState('');
  const [urgent, setUrgent] = useState(message.urgent);  // Track urgency status

  const handleResponseSubmit = () => {
    if (response) {
      onResponseSubmit(message._id, response, urgent);  // Pass urgency along with response
      setResponse('');
    }
  };

  const handleUrgentChange = () => {
    setUrgent(!urgent);  // Toggle the urgent flag
  };

  return (
    <li style={{ backgroundColor: urgent ? 'lightcoral' : 'transparent' }}>
      <strong>{message.customer}</strong> -{' '}
      <em>{new Date(message.timestamp).toLocaleString()}</em>: {message.message}
      <div>
        <input
          type="text"
          placeholder="Enter your response"
          value={response}
          onChange={(e) => setResponse(e.target.value)}  // Update response
        />
        <button onClick={handleResponseSubmit}>Respond</button>
        <div>
          <input
            type="checkbox"
            checked={urgent}
            onChange={handleUrgentChange}  // Handle urgent flag toggle
          />
          <span>Mark as urgent</span>
        </div>
      </div>
    </li>
  );
};

export default MessageItem;
