// src/components/MessageDetails.js
import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';  // useNavigate instead of useHistory

const MessageDetails = () => {
  const [message, setMessage] = useState(null);
  const [response, setResponse] = useState('');
  const [urgent, setUrgent] = useState(false);
  const { messageId } = useParams();  // Get the message ID from the URL
  const navigate = useNavigate(); // Initialize useNavigate hook for navigation

  useEffect(() => {
    // Fetch the selected message details using fetch (instead of axios)
    fetch(`http://localhost:5000/api/messages/${messageId}`)
      .then(response => response.json())
      .then(data => setMessage(data))
      .catch(err => console.error('Error fetching message:', err));
  }, [messageId]);

  const handleResponseSubmit = () => {
    // Send the response and update the status and urgency flag using fetch (instead of axios)
    fetch(`http://localhost:5000/api/messages/${messageId}`, {
      method: 'PUT',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({
        response,
        urgent,
        agentId: 'agent1',  // Replace with actual agent ID
      }),
    })
      .then(response => response.json())
      .then(data => {
        setMessage(data);  // Update the message data with the updated response
      })
      .catch(err => console.error('Error updating message:', err));
  };

  if (!message) return <div>Loading...</div>;

  return (
    <div>
      <h2>Message Details</h2>
      <p><strong>Customer:</strong> {message.customer}</p>
      <p><strong>Message:</strong> {message.message}</p>
      <p><strong>Status:</strong> {message.status}</p>
      <p><strong>Urgent:</strong> {message.urgent ? 'Yes' : 'No'}</p>
      <p><strong>Timestamp:</strong> {new Date(message.timestamp).toLocaleString()}</p>
      
      <textarea 
        value={response} 
        onChange={(e) => setResponse(e.target.value)} 
        placeholder="Write your response"
      />
      
      <div>
        <label>
          Mark as Urgent
          <input 
            type="checkbox" 
            checked={urgent} 
            onChange={() => setUrgent(!urgent)} 
          />
        </label>
      </div>
      
      <button onClick={handleResponseSubmit}>Submit Response</button>
      <button onClick={() => navigate('/')} >Back to Messages</button> {/* Navigate to another page */}
    </div>
  );
};

export default MessageDetails;
