// src/App.js
import React, { useState, useEffect } from 'react';
import MessageForm from './components/MessageForm';  // Import your MessageForm component

const App = () => {
  const [messages, setMessages] = useState([]);

  // Fetch messages when the component mounts (GET request to the backend)
  useEffect(() => {
    fetch('http://localhost:5000/api/messages')  // Make a GET request to backend API
      .then((response) => response.json())
      .then((data) => setMessages(data))  // Store the fetched messages in state
      .catch((error) => console.error('Error fetching messages:', error));
  }, []);

  // Handle form submission (send POST request to backend)


  return (
    <div>
      <h1>Customer Service Messaging</h1>
      <MessageForm onSubmit={handleFormSubmit} />  {/* Pass handleFormSubmit to the MessageForm component */}
      
      <h2>Messages:</h2>
      <ul>
        {messages.map((message, index) => (
          <li key={index}>
            <strong>{message.customer}:</strong> {message.message}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default App;
