// frontend/src/components/MessageItem.js
import React, { useState } from 'react';

const MessageItem = ({ message, onResponseSubmit }) => {
  const [response, setResponse] = useState('');

  const handleResponseSubmit = () => {
    if (response) {
      onResponseSubmit(message._id, response);  // Submit the response to the backend
      setResponse('');  // Clear the input after submission
    }
  };

  return (
    <li>
      <strong>{message.customer}</strong> -{' '}
      <em>{new Date(message.timestamp).toLocaleString()}</em>: {message.message}
      <div>
        <input
          type="text"
          placeholder="Enter your response"
          value={response}
          onChange={(e) => setResponse(e.target.value)}  // Update response value
        />
        <button onClick={handleResponseSubmit}>Respond</button>
      </div>
    </li>
  );
};

export default MessageItem;
