import React, { useState, useEffect } from 'react';

const AgentDashboard = () => {
  const [messages, setMessages] = useState([]);
  const [loading, setLoading] = useState(true);
  const [reply, setReply] = useState('');
  const [selectedMessageId, setSelectedMessageId] = useState(null);
  const [searchQuery, setSearchQuery] = useState('');
  const [isUrgentFilter, setIsUrgentFilter] = useState('');

  // Fetch messages from the server
  useEffect(() => {
    const fetchMessages = async () => {
      setLoading(true);
      try {
        const response = await fetch(`http://localhost:5000/api/messages?isUrgent=${isUrgentFilter}`);
        const data = await response.json();

        // Handle API response structure
        if (data.urgentMessages && data.normalMessages) {
          setMessages([...data.urgentMessages, ...data.normalMessages]);
        } else if (Array.isArray(data)) {
          setMessages(data); // Fallback for flat array
        } else {
          console.error('Unexpected response format:', data);
        }
      } catch (error) {
        console.error('Error fetching messages:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchMessages();
  }, [isUrgentFilter]);

  // Handle search input
  const handleSearchChange = (e) => {
    setSearchQuery(e.target.value);
  };

  // Handle reply submission
  const handleReplySubmit = async (id) => {
    try {
      const response = await fetch(`http://localhost:5000/api/messages/${id}`, {
        method: 'PATCH',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ reply }),
      });

      const data = await response.json();
      if (data.message) {
        alert('Reply sent successfully!');
        setReply('');
        setSelectedMessageId(null);
        setIsUrgentFilter(''); // Reset filter to refresh messages
      } else {
        alert('Failed to send reply.');
      }
    } catch (error) {
      console.error('Error sending reply:', error);
    }
  };

  // Filter messages by search query
  const filteredMessages = messages.filter((message) => {
    const lowerCaseQuery = searchQuery.toLowerCase();
    return (
      message.customer.toLowerCase().includes(lowerCaseQuery) ||
      message.message.toLowerCase().includes(lowerCaseQuery)
    );
  });

  return (
    <div>
      <h2>Agent Dashboard</h2>

      {/* Search bar */}
      <div>
        <input
          type="text"
          value={searchQuery}
          onChange={handleSearchChange}
          placeholder="Search by User ID or Message"
          style={{ padding: '10px', width: '300px', marginBottom: '20px' }}
        />
      </div>

      {/* Urgency filter */}
      <div>
        <button onClick={() => setIsUrgentFilter('true')} style={{ marginRight: '10px' }}>
          Show Urgent Messages
        </button>
        <button onClick={() => setIsUrgentFilter('false')} style={{ marginRight: '10px' }}>
          Show Non-Urgent Messages
        </button>
        <button onClick={() => setIsUrgentFilter('')} style={{ marginRight: '10px' }}>
          Show All Messages
        </button>
      </div>

      <div>
        {loading ? (
          <p>Loading messages...</p>
        ) : filteredMessages.length > 0 ? (
          <ul>
            {filteredMessages.map((message) => (
              <li key={message._id} style={{ marginBottom: '20px', borderBottom: '1px solid #ccc' }}>
                <p><strong>User ID:</strong> {message.customer}</p>
                <p><strong>Message:</strong> {message.message}</p>
                <p><strong>Timestamp:</strong> {new Date(message.timestamp).toLocaleString()}</p>
                <p><strong>Status:</strong> {message.status}</p>

                {message.status !== 'resolved' && (
                  <button onClick={() => setSelectedMessageId(message._id)}>
                    Reply
                  </button>
                )}

                {selectedMessageId === message._id && (
                  <div>
                    <textarea
                      value={reply}
                      onChange={(e) => setReply(e.target.value)}
                      placeholder="Type your reply here..."
                      rows="3"
                      style={{ width: '100%', marginTop: '10px' }}
                    />
                    <button
                      onClick={() => handleReplySubmit(message._id)}
                      style={{ marginTop: '10px' }}
                    >
                      Submit Reply
                    </button>
                  </div>
                )}
              </li>
            ))}
          </ul>
        ) : (
          <p>No messages to display.</p>
        )}
      </div>
    </div>
  );
};

export default AgentDashboard;
