import React, { useState } from 'react';

const AgentLogin = ({ onLogin }) => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();

    // Perform your login logic here, e.g., check credentials
    if (username === 'agent' && password === 'password') {
      onLogin();  // Call the onLogin function passed from App.js
      window.location.href = '/agent-dashboard';  // Redirect to dashboard after successful login
    } else {
      alert('Invalid credentials');
    }
  };

  return (
    <div>
      <h2>Agent Login</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Username:</label>
          <input
            type="text"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
          />
        </div>
        <div>
          <label>Password:</label>
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
        </div>
        <button type="submit">Login</button>
      </form>
    </div>
  );
};

export default AgentLogin;
