import React, { useState, useEffect } from 'react';
import AgentLogin from './components/AgentLogin';
import MessageForm from './components/MessageForm';
import MessageItem from './components/MessageItem';

const App = () => {
  const [messages, setMessages] = useState([]);
  const [agentId, setAgentId] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  // Fetch messages assigned to the logged-in agent
  useEffect(() => {
    if (agentId) {
      setLoading(true);
      setError(null);
      fetch(`http://localhost:5000/api/messages?agentId=${agentId}`)
        .then((response) => response.json())
        .then((data) => {
          setMessages(data);
          setLoading(false);
        })
        .catch((error) => {
          setError('Error fetching messages');
          setLoading(false);
        });
    }
  }, [agentId]);

  const handleFormSubmit = (formData) => {
    setLoading(true);
    fetch('http://localhost:5000/api/messages', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(formData),
    })
      .then((response) => response.json())
      .then((data) => {
        setMessages((prevMessages) => [...prevMessages, data]);
        setLoading(false);
      })
      .catch((error) => {
        setError('Error posting message');
        setLoading(false);
      });
  };

  const handleLogin = (agentId) => {
    setAgentId(agentId);  // Set the logged-in agent ID
  };

  // Handle response submission for a message
  const handleResponseSubmit = (messageId, response) => {
    const cleanedMessageId = messageId.trim();
    
    fetch(`http://localhost:5000/api/messages/${cleanedMessageId}`, {
      method: 'PUT',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ response, agentId }),
    })
      .then((response) => response.json())
      .then((updatedMessage) => {
        setMessages((prevMessages) =>
          prevMessages.map((message) =>
            message._id === updatedMessage._id ? updatedMessage : message
          )
        );
      })
      .catch((error) => console.error('Error updating message:', error));
  };

  return (
    <div>
      {!agentId ? (
        <AgentLogin onLogin={handleLogin} />
      ) : (
        <>
          <h1>Welcome Agent {agentId}</h1>
          <MessageForm onSubmit={handleFormSubmit} />
          
          {loading && <p>Loading messages...</p>}
          {error && <p>{error}</p>}

          <h2>Messages:</h2>
          <ul>
            {messages.map((message) => (
              <MessageItem
                key={message._id}
                message={message}
                onResponseSubmit={handleResponseSubmit}
                isUrgent={message.urgent}  // Pass the urgent flag to MessageItem
              />
            ))}
          </ul>
        </>
      )}
    </div>
  );
};

export default App;
