// src/components/MessageDetails.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams, useHistory } from 'react-router-dom';

const MessageDetails = () => {
  const [message, setMessage] = useState(null);
  const [response, setResponse] = useState('');
  const [urgent, setUrgent] = useState(false);
  const [error, setError] = useState(null); // For error handling
  const { messageId } = useParams();  // Get the message ID from the URL
  const history = useHistory(); // For navigation after submission

  useEffect(() => {
    // Fetch the selected message details
    axios.get(`http://localhost:5000/api/messages/${messageId}`)
      .then(response => {
        setMessage(response.data);
      })
      .catch(err => {
        console.error('Error fetching message:', err);
        setError('Failed to load message details');
      });
  }, [messageId]);

  const handleResponseSubmit = () => {
    if (!response.trim()) {
      setError('Please enter a response before submitting.');
      return;
    }

    // Send the response and update the status and urgency flag
    axios.put(`http://localhost:5000/api/messages/${messageId}`, {
      response,
      urgent,
      agentId: 'agent1',  // Replace with actual agent ID
    })
    .then(response => {
      setMessage(response.data);  // Update the message data with the updated response
      setResponse('');  // Clear the response field
      setUrgent(false);  // Reset the urgency flag
      setError(null);  // Clear any previous errors
      history.push('/'); // Navigate back to the main page or message list
    })
    .catch(err => {
      console.error('Error updating message:', err);
      setError('Failed to submit response.');
    });
  };

  if (!message) return <div>Loading...</div>;

  return (
    <div>
      <h2>Message Details</h2>
      {error && <div style={{ color: 'red' }}>{error}</div>} {/* Show error message if any */}

      <p><strong>Customer:</strong> {message.customer}</p>
      <p><strong>Message:</strong> {message.message}</p>
      <p><strong>Status:</strong> {message.status}</p>
      <p><strong>Urgent:</strong> {message.urgent ? 'Yes' : 'No'}</p>
      <p><strong>Timestamp:</strong> {new Date(message.timestamp).toLocaleString()}</p>

      <textarea 
        value={response} 
        onChange={(e) => setResponse(e.target.value)} 
        placeholder="Write your response"
      />
      
      <div>
        <label>
          Mark as Urgent
          <input 
            type="checkbox" 
            checked={urgent} 
            onChange={() => setUrgent(!urgent)} 
          />
        </label>
      </div>
      
      <button onClick={handleResponseSubmit}>Submit Response</button>
    </div>
  );
};

export default MessageDetails;
