import React from 'react';
import './AgentDashboard.css'; // Add this line to import the CSS

const MessageItem = ({ message, onClick }) => (
  <div className="message-item" onClick={() => onClick(message)}>
    <h3>{message.customer}</h3>
    <p>{message.message}</p>
  </div>
);

const AgentDashboard = ({ messages, onMessageClick }) => {
  return (
    <div className="agent-dashboard">
      <h2>Welcome, Agent</h2>
      <h3>Your Messages:</h3>
      <div className="message-list">
        {messages.map((message) => (
          <MessageItem key={message._id} message={message} onClick={onMessageClick} />
        ))}
      </div>
    </div>
  );
};

export default AgentDashboard;
