// src/components/MessageForm.js
import React, { useState } from 'react';

const MessageForm = ({ onSubmit }) => {
  const [userId, setUserId] = useState(''); // Changed to userId
  const [message, setMessage] = useState('');
  const [agentId, setAgentId] = useState(''); // New state for agent ID

  const handleSubmit = (e) => {
    e.preventDefault();
    const formData = { userId, message, agentId };  // Include userId instead of customer
    onSubmit(formData);
    setUserId('');
    setMessage('');
    setAgentId('');  // Clear agent ID after submission
  };

  return (
    <form onSubmit={handleSubmit}>
      <input
        type="text"
        placeholder="User ID"  // Changed from 'Customer Name'
        value={userId}
        onChange={(e) => setUserId(e.target.value)}
        required
      />
      <textarea
        placeholder="Message"
        value={message}
        onChange={(e) => setMessage(e.target.value)}
        required
      />
      <input
        type="text"
        placeholder="Agent ID (Optional)"
        value={agentId}
        onChange={(e) => setAgentId(e.target.value)}
      />
      <button type="submit">Send</button>
    </form>
  );
};

export default MessageForm;
