// UserQueryForm.js (Frontend)
import React, { useState } from 'react';

const UserQueryForm = () => {
  const [userId, setUserId] = useState('');
  const [message, setMessage] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!userId || !message) {
      alert('Both fields are required!');
      return;
    }

    try {
      const response = await fetch('http://localhost:5000/api/messages', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ userId, message }), // Send userId and message
      });

      if (response.ok) {
        alert('Query submitted successfully!');
        setUserId('');
        setMessage('');
      } else {
        const errorData = await response.json();
        alert(`Failed to submit the query: ${errorData.error}`);
      }
    } catch (error) {
      console.error('Error:', error);
      alert('An error occurred while submitting the query. Please try again.');
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <input
        type="text"
        value={userId}
        onChange={(e) => setUserId(e.target.value)}
        placeholder="User ID"
        required
      />
      <textarea
        value={message}
        onChange={(e) => setMessage(e.target.value)}
        placeholder="Your message"
        required
      ></textarea>
      <button type="submit">Submit</button>
    </form>
  );
};

export default UserQueryForm;
