import React, { useState, useEffect } from 'react';

const AgentDashboard = () => {
  const [messages, setMessages] = useState([]);
  const [isUrgentFilter, setIsUrgentFilter] = useState('');
  const [loading, setLoading] = useState(false);
  const [searchQuery, setSearchQuery] = useState('');
  const [reply, setReply] = useState('');

  // Fetch messages from the backend with urgency filter
  // Re-fetch when the urgency filter changes

  // Filter messages based on search query (User ID or message content)
  const filteredMessages = messages.filter((message) => {
    const lowerCaseQuery = searchQuery.toLowerCase();
    return (
      message.customer.toLowerCase().includes(lowerCaseQuery) ||
      message.message.toLowerCase().includes(lowerCaseQuery)
    );
  });

  // Handle reply submission
  const handleReplySubmit = async (messageId) => {
    try {
      const response = await fetch(`http://localhost:5000/api/messages/${messageId}`, {
        method: 'PATCH',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ reply }),
      });

      const data = await response.json();
      if (data.message === 'Reply sent and status updated') {
        alert('Reply sent successfully!');
        setReply(''); // Clear the reply input
        setIsUrgentFilter(''); // Optionally reset the filter after replying
      }
    } catch (error) {
      console.error("Error sending reply:", error);
    }
  };

  // Button for showing urgent messages
  const handleUrgentMessages = () => setIsUrgentFilter('true');
  const handleNormalMessages = () => setIsUrgentFilter('false');
  const handleAllMessages = () => setIsUrgentFilter('');

  return (
    <div>
      <h2>Agent Dashboard</h2>

      {/* Search Bar */}
      <input
        type="text"
        value={searchQuery}
        onChange={(e) => setSearchQuery(e.target.value)}
        placeholder="Search by User ID or Message..."
        style={{ width: '300px', padding: '5px', marginBottom: '15px' }}
      />

      {/* Filter Buttons */}
      <div>
        <button onClick={handleUrgentMessages} style={{ marginRight: '10px' }}>
          Show Urgent Messages
        </button>
        <button onClick={handleNormalMessages} style={{ marginRight: '10px' }}>
          Show Normal Messages
        </button>
        <button onClick={handleAllMessages} style={{ marginRight: '10px' }}>
          Show All Messages
        </button>
      </div>

      {/* Message List */}
      {loading ? (
        <p>Loading messages...</p>
      ) : (
        <div>
          {filteredMessages.length > 0 ? (
            <ul>
              {filteredMessages.map((message) => (
                <li key={message._id} style={{ marginBottom: '20px' }}>
                  <div>
                    <strong>User ID: {message.customer}</strong>
                  </div>
                  <div>
                    <strong>Message:</strong> {message.message}
                  </div>
                  <div>
                    <strong>Timestamp:</strong> {new Date(message.timestamp).toLocaleString()}
                  </div>

                  {/* Reply Section */}
                  <textarea
                    value={reply}
                    onChange={(e) => setReply(e.target.value)}
                    placeholder="Type your reply here..."
                    rows="3"
                    style={{ width: '100%', marginTop: '10px' }}
                  />
                  <button
                    onClick={() => handleReplySubmit(message._id)}
                    style={{ marginTop: '10px' }}
                  >
                    Submit Reply
                  </button>
                </li>
              ))}
            </ul>
          ) : (
            <p>No messages to display.</p>
          )}
        </div>
      )}
    </div>
  );
};

export default AgentDashboard;
