// frontend/src/components/AgentLogin.js
import React, { useState } from 'react';

const AgentLogin = ({ onLogin }) => {
  const [agentId, setAgentId] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    if (agentId.trim()) {
      onLogin(agentId);  // Pass the agentId back to App.js to set the logged-in agent
    }
  };

  return (
    <div>
      <h2>Agent Login</h2>
      <form onSubmit={handleSubmit}>
        <label>
          Agent ID:
          <input
            type="text"
            value={agentId}
            onChange={(e) => setAgentId(e.target.value)}
            required
          />
        </label>
        <button type="submit">Login</button>
      </form>
    </div>
  );
};

export default AgentLogin;
